ewcommand{\KEY}[1]{	extcolor{Gray}{	extsf{#1}}} 
ewcommand{\LEX}[1]{	extcolor{Gray}{	extsf{}}	exttt{#1}	extcolor{Gray}{	extsf{}}} 
ewcommand{\VAR}[1]{	extit{#1}} 
ewcommand{\NAME}[1]{	extcolor{DarkRed}{	extsf{#1}}} 
ewcommand{\SYN}[1]{	extcolor{DarkGreen}{	extsf{#1}}} 
ewcommand{\SEM}[1]{	extcolor{DarkBlue}{	extsf{#1}}} 
ewcommand{\LEFTPHRASE}{[\![} 
