ewcommand{\SYN}[1]{\STYLE{SyntaxName}{	extsf{#1}}} 
ewcommand{\SEM}[1]{\STYLE{SemanticsName}{	extsf{#1}}} 
ewcommand{\SECT}[1]{\STYLE{SectionNumber}{	extsf{#1}}} 
ewcommand{\LANG}[1]{\STYLE{Language}{#1}} % DEC 
ewcommand{\DEC}[3]{\smash{aise{2.4ex}{\cssId{#1:#2}{}}}#3} 
ewcommand{\VARDEC}[1]{\DEC{PartVariable}{#1}{\VAR{#1}}} 
ewcommand{\FUNDEC}[1]{\DEC{Name}{#1}{\FUN{#1}}} 
ewcommand{\SYNDEC}[1]{\DEC{SyntaxName}{#1}{\SYN{#1}}} 
ewcommand{\SEMDEC}[1]{\DEC{SemanticsName}{#1}{\SEM{#1}}} 
ewcommand{\SECTDEC}[1]{\DEC{SectionNumber}{#1}{	extsf{#1}}} % 
ewcommand{\LANGDEC}[1]{\DEC{Language}{#1}{\LANG{#1}}} % REF 
ewcommand{\REF}[3]{\href{###1%3A#2}{#3}} 
ewcommand{\VARREF}[1]{\REF{PartVariable}{#1}{\VAR{#1}}} 
ewcommand{\FUNREF}[1]{\REF{Name}{#1}{\FUN{#1}}} 
ewcommand{\SYNREF}[1]{\REF{SyntaxName}{#1}{\SYN{#1}}} 
ewcommand{\SEMREF}[1]{\REF{SemanticsName}{#1}{\SEM{#1}}} 
ewcommand{\SECTREF}[1]{\REF{SectionNumber}{#1}{\SECT{#1}}} % 
ewcommand{\LANGREF}[1]{\REF{Language}{#1}{\LANG{#1}}} % HYP 
ewcommand{\HYP}[5]{\href{#1/#2/index.html###3%3A#4}{#5}} 
ewcommand{\VARHYP}[3]{\HYP{#1}{#2}{PartVariable}{#3}{\VAR{#3}}} 
ewcommand{\FUNHYP}[3]{\HYP{#1}{#2}{Name}{#3}{\FUN{#3}}} 
ewcommand{\SYNHYP}[3]{\HYP{#1}{#2}{SyntaxName}{#3}{\SYN{#3}}} 
ewcommand{\SEMHYP}[3]{\HYP{#1}{#2}{SemanticsName}{#3}{\SEM{#3}}} 
ewcommand{\SECTHYP}[3]{\HYP{#1}{#2}{SectionNumber}{#3}{\SECT{#3}}} % 
ewcommand{\LANGHYP}[3]{\HYP{#1}{#2}{Language}{#3}{\LANG{#3}}} % CBS-beta/math hyperlinks 
ewcommand{\CBSBETAMATH}{https://plancomps.github.io/CBS-beta/math} 
ewcommand{\VARCBS}[3]{\VARHYP{\CBSBETAMATH/#1}{#2}{#3}} 
ewcommand{\FUNCBS}[3]{\FUNHYP{\CBSBETAMATH/#1}{#2}{#3}} 
ewcommand{\SYNCBS}[3]{\SYNHYP{\CBSBETAMATH/#1}{#2}{#3}} 
ewcommand{\SEMCBS}[3]{\SEMHYP{\CBSBETAMATH/#1}{#2}{#3}} 
ewcommand{\SECTCBS}[3]{\SECTHYP{\CBSBETAMATH/#1}{#2}{#3}} % 
ewcommand{\LANGCBS}[3]{\LANGHYP{\CBSBETAMATH/#1}{#2}{#3}} % \LEFTPHRASE MATH \RIGHTPHRASE produces [[ MATH ]] with proper brackets: 
ewcommand{\LEFTPHRASE}{\llbracket} 
ewcommand{\RIGHTPHRASE}{rbracket} % \LEFTGROUP MATH \RIGHTGROUP produces ( MATH ) where the parentheses are % highlighted the same as keywords: 
ewcommand{\LEFTGROUP}{\STYLE{Key}{(}} 
ewcommand{\RIGHTGROUP}{\STYLE{Key}{)}} % MATH\PLUS produces a superscript + % MATH\STAR produces a superscript * % MATH\QUERY produces a superscript ? 
ewcommand{\PLUS}{ {}^{	exttt{+}}} 
ewcommand{\STAR}{ {}^{	exttt{*}}} 
ewcommand{\QUERY}{ {}^{	exttt{?}}} % \RULE{premise}{conclusion} produces % premise % ---------- % conclusion 
ewcommand{\RULE}[2]{ rac{\displaystyle#1}{\displaystyle#2}} % See https://tex.stackexchange.com/questions/337328/superscripts-appear-in-various-weird-places-in-fractions % \RULE % { egin{aligned} &amp; premise \ &amp; ... \end{aligned}} % { egin{aligned} &amp; conclusion ... \ &amp; ... \end{aligned}} % produces an inference rule with left-aligned premises and split conclusion % premise % ... % -------------- % conclusion ... % ... % \TO TYPE produces =&gt; TYPE 
ewcommand{\TO}{\mathop{\Rightarrow}} % TERM \TRANS TERM produces TERM ---&gt; TERM 
ewcommand{\TRANS}{\longrightarrow} % TERM \xrightarrow{LABEL} TERM puts the label above the long arrow .
